function new_log($message) {
	$pluginlog = 'debug.log';	
	if (is_array($message) || is_object($message)) :
		$output = print_r($message, true);
	else:
		$output = $message.PHP_EOL;
	endif;
	error_log($output, 3, $pluginlog);
}

function is_totem() {
	if(isset($_GET) && isset($_GET['totem']) && $_GET['totem'] == 1) :
		return true;
	endif;
	return false;
}
